import React, { useEffect, useRef } from "react";
import { useParams } from "react-router-dom";

const KibanaPage = () => {
  const { datacenterId } = useParams(); // Récupère le paramètre de route
  const iframeContainerRef = useRef(null);

  useEffect(() => {
    // Crée un iframe avec le lien du dashboard spécifique en fonction de datacenterId
    const iframe = document.createElement("iframe");

    if (datacenterId === "datacenter1") {
      iframe.src =
        "http://localhost:5601/app/dashboards#/view/a4ed68f0-caad-11ef-9211-1ba9584b0596?embed=true&_g=(refreshInterval:(pause:!f,value:10000),time:(from:'2008-11-08T23:00:00.000Z',to:'2008-11-11T22:30:00.000Z'))&_a=(viewMode:edit)";
    } else if (datacenterId === "datacenter2") {
      iframe.src =
        "http://localhost:5601/app/dashboards#/view/ff498ed0-cbb3-11ef-bbfc-932b85a4781a?_g=(refreshInterval:(pause:!f,value:10000),time:(from:'2014-12-31T23:00:00.000Z',to:'2017-12-11T22:30:00.000Z'))&_a=(viewMode:edit)";
    }

    iframe.style.width = "100%";
    iframe.style.height = "100%";
    iframe.style.border = "none";
    iframe.title = "Kibana Dashboard";

    if (iframeContainerRef.current) {
      iframeContainerRef.current.appendChild(iframe);
    }

    return () => {
      if (iframeContainerRef.current) {
        iframeContainerRef.current.innerHTML = "";
      }
    };
  }, [datacenterId]); // Effectue une mise à jour si datacenterId change

  return (
    <div style={{ width: "100vw", height: "100vh", display: "flex", flexDirection: "column" }}>
      <header style={{ textAlign: "center", padding: "20px 0", backgroundColor: "#170a88", fontSize: "24px", color: "white" }}>
        {datacenterId === "datacenter1" ? "Visualisation du Data Center 1" : "Visualisation du Data Center 2"}
      </header>
      <div ref={iframeContainerRef} style={{ flex: 1, display: "flex", justifyContent: "center", alignItems: "center" }}></div>
    </div>
  );
};

export default KibanaPage;
