import React, { useEffect, useRef } from "react";

const KibanaPage = () => {
  const iframeContainerRef = useRef(null);

  useEffect(() => {
    // Crée un iframe
    const iframe = document.createElement("iframe");
    iframe.src =
      "http://localhost:5601/app/dashboards#/view/a4ed68f0-caad-11ef-9211-1ba9584b0596?embed=true&_g=(refreshInterval:(pause:!f,value:10000),time:(from:'2008-11-08T23:00:00.000Z',to:'2008-11-11T22:30:00.000Z'))&_a=(viewMode:edit)" ,height="600" ,width="800";
    iframe.style.width = "100%"; // Le iframe occupera toute la largeur
    iframe.style.height = "100%"; // Le iframe occupera toute la hauteur
    iframe.style.border = "none"; // Enlève la bordure
    iframe.title = "Kibana Dashboard";

    // Ajoute l'iframe au conteneur si disponible
    if (iframeContainerRef.current) {
      iframeContainerRef.current.appendChild(iframe);
    }

    // Nettoyage lors du démontage
    return () => {
      if (iframeContainerRef.current) {
        iframeContainerRef.current.innerHTML = "";
      }
    };
  }, []);

  return (
    <div
      style={{
        width: "100vw", // Prend toute la largeur de l'écran
        height: "100vh", // Prend toute la hauteur de l'écran
        margin: 0,
        padding: 0,
        overflow: "hidden", // Évite les barres de défilement
        display: "flex",
        flexDirection: "column", // Disposition verticale
      }}
    >
      {/* Titre de la page */}
      <header
        style={{
          textAlign: "center", // Aligne le texte au centre
          padding: "20px 0", // Ajoute de l'espace autour du texte
          backgroundColor: "#170a88", // Couleur de fond
          fontSize: "24px", // Taille de la police
          fontWeight: "bold", // Met le texte en gras
          borderBottom: "1px solid #ddd", // Bordure en bas
          color: "white", // Couleur du texte en blanc
        }}
      >
       Visualisations du Data Center 2
      </header>

      {/* Conteneur de l'iframe */}
      <div
        id="iframe-container"
        ref={iframeContainerRef}
        style={{
          flex: 1, // Prend tout l'espace restant
          display: "flex",
          justifyContent: "center", // Centre l'iframe horizontalement
          alignItems: "center", // Centre l'iframe verticalement
        }}
      ></div>
    </div>
  );
};

export default KibanaPage;
